class Localbeach < Formula
  desc "Command-line tool for Flownative Beach"
  homepage "https://www.flownative.com/beach"
  license "GPL-3.0-or-later"
  version "v1.10.0+1"

  if RUBY_PLATFORM.downcase.include?("darwin")
    url "https://github.com/flownative/localbeach/releases/download/v1.10.0+1/beach_darwin_amd64.zip"
    sha256 "01b04d24b542b10bc6646f0b67f95c9545d2e5770d9c9ee29d307f2ab2a94e7d"
  else
    url "https://github.com/flownative/localbeach/releases/download/v1.10.0+1/beach_linux_amd64.zip"
    sha256 "019c45a68dced02bbbeb5b609216c514d88a15290bbdfdb294b68e653ec90b6a"
  end

  bottle :unneeded

  conflicts_with "flownative/flownative/beach-cli", because: "localbeach replaces beach-cli"

  depends_on "mkcert" => :run
  depends_on "nss" => :run

  def install
    bin.install "beach" => "beach"
  end

  def caveats
  <<~EOS
Local Beach is built on top of Docker and Docker Compose. You will need a working setup of both in order to use Local
Beach.
  EOS
  end
end
