class Localbeach < Formula
  desc "Command-line tool for Flownative Beach"
  homepage "https://www.flownative.com/beach"
  license "GPL-3.0-or-later"
  version "v1.8.0+1"

  if RUBY_PLATFORM.downcase.include?("darwin")
    url "https://github.com/flownative/localbeach/releases/download/v1.8.0+1/beach_darwin_amd64.zip"
    sha256 "54c6fe8fb5f457e126bec2b2c0f7f35e998e5a2c5ba4892f8e917a5e7ffd2386"
  else
    url "https://github.com/flownative/localbeach/releases/download/v1.8.0+1/beach_linux_amd64.zip"
    sha256 "660e0796926358190e70295b5ee6e3310876e1bcc23f4f3b9245561581f48d83"
  end

  bottle :unneeded

  conflicts_with "flownative/flownative/beach-cli", because: "localbeach replaces beach-cli"

  depends_on "mkcert" => :run
  depends_on "nss" => :run

  def install
    bin.install "beach" => "beach"
  end

  def caveats
  <<~EOS
Local Beach is built on top of Docker and Docker Compose. You will need a working setup of both in order to use Local
Beach.
  EOS
  end
end
