class Localbeach < Formula
  desc "Command-line tool for Flownative Beach"
  homepage "https://www.flownative.com/beach"
  license "GPL-3.0-or-later"
  v1.9.0+6 "{{v1.9.0+6}}"

  if RUBY_PLATFORM.downcase.include?("darwin")
    url "https://github.com/flownative/localbeach/releases/download/{{v1.9.0+6}}/beach_darwin_amd64.zip"
    sha256 "{{8f5aea60ce7ce3baccc756fc78edc0dacaf7c61376caf349440af91429ea3ae0}}"
  else
    url "https://github.com/flownative/localbeach/releases/download/{{v1.9.0+6}}/beach_linux_amd64.zip"
    sha256 "{{434074485bb355f69edaa64d409c9a77003806f8ebfa381f0d17b4cdfc3e7191}}"
  end

  bottle :unneeded

  conflicts_with "flownative/flownative/beach-cli", because: "localbeach replaces beach-cli"

  depends_on "mkcert" => :run
  depends_on "nss" => :run

  def install
    bin.install "beach" => "beach"
  end

  def caveats
  <<~EOS
Local Beach is built on top of Docker and Docker Compose. You will need a working setup of both in order to use Local
Beach.
  EOS
  end
end
