class Localbeach < Formula
  desc "Command-line tool for Flownative Beach"
  homepage "https://www.flownative.com/beach"
  license "GPL-3.0-or-later"
  version "v1.10.1+2"

  if RUBY_PLATFORM.downcase.include?("darwin")
    url "https://github.com/flownative/localbeach/releases/download/v1.10.1+2/beach_darwin_amd64.zip"
    sha256 "31023d694b5a5e13138f95de8a69e6c29f3bbf707ed4602b73a5a56e82d6f9dd"
  else
    url "https://github.com/flownative/localbeach/releases/download/v1.10.1+2/beach_linux_amd64.zip"
    sha256 "16444b9c23042be350ff66828a39e0642ba0360c836fa5eaeb3589a64dba7056"
  end

  bottle :unneeded

  conflicts_with "flownative/flownative/beach-cli", because: "localbeach replaces beach-cli"

  depends_on "mkcert" => :run
  depends_on "nss" => :run

  def install
    bin.install "beach" => "beach"
  end

  def caveats
  <<~EOS
Local Beach is built on top of Docker and Docker Compose. You will need a working setup of both in order to use Local
Beach.
  EOS
  end
end
