class Localbeach < Formula
  desc "Command-line tool for Flownative Beach"
  homepage "https://www.flownative.com/beach"
  license "GPL-3.0-or-later"
  version "v1.9.0+8"

  if RUBY_PLATFORM.downcase.include?("darwin")
    url "https://github.com/flownative/localbeach/releases/download/v1.9.0+8/beach_darwin_amd64.zip"
    sha256 "b8031a12b7f92bd2cdfccc63d27d15ea1c5d7b30afaed03c2484fcb7f0deeafd"
  else
    url "https://github.com/flownative/localbeach/releases/download/v1.9.0+8/beach_linux_amd64.zip"
    sha256 "0b4680f95bbd83212961e9d47ecbf00348ec75789dede1213aa65e71aa577d4c"
  end

  bottle :unneeded

  conflicts_with "flownative/flownative/beach-cli", because: "localbeach replaces beach-cli"

  depends_on "mkcert" => :run
  depends_on "nss" => :run

  def install
    bin.install "beach" => "beach"
  end

  def caveats
  <<~EOS
Local Beach is built on top of Docker and Docker Compose. You will need a working setup of both in order to use Local
Beach.
  EOS
  end
end
