class Localbeach < Formula
  desc "Command-line tool for Flownative Beach"
  homepage "https://www.flownative.com/beach"
  license "GPL-3.0-or-later"
  version "v1.9.0+7"

  if RUBY_PLATFORM.downcase.include?("darwin")
    url "https://github.com/flownative/localbeach/releases/download/v1.9.0+7/beach_darwin_amd64.zip"
    sha256 "85004b41fa50145cea86eaea9f8eb35fd99b7ab19b2997f79810919e03908928"
  else
    url "https://github.com/flownative/localbeach/releases/download/v1.9.0+7/beach_linux_amd64.zip"
    sha256 "ed7ac98f7b6af8a698b4751cc5c047800a68b30237ce0afcc1ef998f88c41fb5"
  end

  bottle :unneeded

  conflicts_with "flownative/flownative/beach-cli", because: "localbeach replaces beach-cli"

  depends_on "mkcert" => :run
  depends_on "nss" => :run

  def install
    bin.install "beach" => "beach"
  end

  def caveats
  <<~EOS
Local Beach is built on top of Docker and Docker Compose. You will need a working setup of both in order to use Local
Beach.
  EOS
  end
end
