class Localbeach < Formula
  desc "Command-line tool for Flownative Beach"
  homepage "https://www.flownative.com/beach"
  license "GPL-3.0-or-later"
  version "v1.11.0+2"

  if RUBY_PLATFORM.downcase.include?("darwin")
    url "https://github.com/flownative/localbeach/releases/download/v1.11.0+2/beach_darwin_amd64.zip"
    sha256 "1f9f7f3be8eb13b7b591c8dc156811de9674df6d67c2e620671f53b30597f67c"
  else
    url "https://github.com/flownative/localbeach/releases/download/v1.11.0+2/beach_linux_amd64.zip"
    sha256 "7e8dc0a2c5ded072a7095a2eb4f26c1da117c3979fc296eacb18c341ee99f514"
  end

  bottle :unneeded

  conflicts_with "flownative/flownative/beach-cli", because: "localbeach replaces beach-cli"

  depends_on "mkcert" => :run
  depends_on "nss" => :run

  def install
    bin.install "beach" => "beach"
  end

  def caveats
  <<~EOS
Local Beach is built on top of Docker and Docker Compose. You will need a working setup of both in order to use Local
Beach.
  EOS
  end
end
