    sha256 "b7f23f0c3d42d43c55b870ce8fbe96be5ccb37d299d7c36b7cf51f34887d9f4a"
  else
    url "https://github.com/flownative/localbeach/releases/download/v1.14.0+1/beach_linux_amd64.zip"
