    sha256 "2770b68e8ad498ee634e85f36e2b1ac97412ff96f3bc0219e0c16d5af4e94174"
  else
    url "https://github.com/flownative/localbeach/releases/download/v1.13.0+1/beach_linux_amd64.zip"
