class Localbeach < Formula
  desc "Command-line tool for Flownative Beach"
  homepage "https://www.flownative.com/beach"
  license "GPL-3.0-or-later"
  version "v1.11.1+1"

  if RUBY_PLATFORM.downcase.include?("darwin")
    url "https://github.com/flownative/localbeach/releases/download/v1.11.1+1/beach_darwin_amd64.zip"
    sha256 "e94dc146c2711712fbf27b33177693540c3558c1e056b52c6b90ef9d45c76065"
  else
    url "https://github.com/flownative/localbeach/releases/download/v1.11.1+1/beach_linux_amd64.zip"
    sha256 "26794e947ee1059981fada6ce2ce6049695ec4f20c3be3409dfd1e7a92f87422"
  end

  bottle :unneeded

  conflicts_with "flownative/flownative/beach-cli", because: "localbeach replaces beach-cli"

  depends_on "mkcert" => :run
  depends_on "nss" => :run

  def install
    bin.install "beach" => "beach"
  end

  def caveats
  <<~EOS
Local Beach is built on top of Docker and Docker Compose. You will need a working setup of both in order to use Local
Beach.
  EOS
  end
end
